// Language icon and color functions for GitHub repositories
export function getLanguageIcon(language: string | null): string {
  if (!language) return "";

  const languageIconMap: Record<string, keyof typeof icons> = {
    JavaScript: "javascript",
    TypeScript: "typescript",
    Vue: "vue",
    React: "react",
    HTML: "html",
    CSS: "css",
    Python: "python",
    Astro: "astro",
    Svelte: "svelte",
    "Node.js": "nodejs",
    PHP: "php",
    Go: "go",
    Rust: "rust",
    Docker: "docker",
    MongoDB: "mongodb",
    TailwindCSS: "tailwindcss",
    Lua: "lua",
    "C++": "cpp",
    "C#": "csharp",
    "Discord.js": "discordjs",
  };

  const iconKey = languageIconMap[language];
  return iconKey ? icons[iconKey] : "";
}

export function getLanguageColor(language: string | null): string {
  if (!language) return "#6b7280";

  const colors: Record<string, string> = {
    JavaScript: "#f1e05a",
    TypeScript: "#3178c6",
    Vue: "#4fc08d",
    React: "#61dafb",
    HTML: "#e34f26",
    CSS: "#1572b6",
    Python: "#3776ab",
    Astro: "#ff5d01",
    Svelte: "#ff3e00",
    "Node.js": "#339933",
    PHP: "#777bb4",
    Go: "#00add8",
    Rust: "#dea584",
    Docker: "#2496ed",
    MongoDB: "#47a248",
    TailwindCSS: "#06b6d4",
    Lua: "#000080",
    "C++": "#00599C",
    "C#": "#239120",
    "Discord.js": "#5865f2",
  };

  return colors[language] || "#6b7280";
}

export function getIcon(iconName: keyof typeof icons): string {
  return icons[iconName] || "";
}
